    rfpulse[RFB_RFSHIM_SLOT].abswidth = 0.8018;
    rfpulse[RFB_RFSHIM_SLOT].effwidth = 0.6755;
    rfpulse[RFB_RFSHIM_SLOT].area = 0.8018;
