    res_rfbrs  = _res_rfbrs.fixedflag ?  ((void)(1000), res_rfbrs) : 1000;
    res_thetarfbrs  = _res_thetarfbrs.fixedflag ?  ((void)(res_rfbrs), res_thetarfbrs) : res_rfbrs;

