    amp_rt_dbdtopt(_cal_amplimit.fixedflag ? (_temp402_cal_amplimit=cal_amplimit,&_temp402_cal_amplimit) : &cal_amplimit, &target_risetime, cal_slewrate, 1.0, calloggrd.tz_xyz);
